import React, { useState, useEffect } from "react";
import Header from "./components/Header.js";
import SearchForm from "./components/SearchForm.js";
import AddRocketForm from "./components/AddRocketForm.js";
import RocketCards from "./components/RocketCards.js";
import Modal from "./components/Modal.js";
import SortForm from "./components/SortForm.js";
import "./App.css";

function App() {
  const [rockets, setRockets] = useState([]);

  // Функція для отримання всіх ракет або виконання пошуку
  const fetchRockets = async (searchParams = {}) => {
    let query = "";

    // Додаємо параметри до запиту, якщо є
    if (searchParams.name) {
      query = `?name=${searchParams.name}`;
    }

    try {
      const response = await fetch(`http://localhost:8080/rockets${query}`);
      const data = await response.json();
      setRockets(data);
    } catch (error) {
      console.error("Error fetching rockets:", error);
    }
  };

  // Викликаємо функцію для завантаження всіх ракет при першому завантаженні сторінки
  useEffect(() => {
    fetchRockets(); // Завантажуємо всі ракети при першому рендері
  }, []);

  return (
    <div className="App">
      <Header />
      <SearchForm onSearch={fetchRockets} /> {/* Передаємо функцію пошуку */}
      <SortForm onSort={fetchRockets} />
      <AddRocketForm />
      <RocketCards rockets={rockets} />
      <Modal />
    </div>
  );
}

export default App;
