import React, { useState, useEffect } from "react";
import RocketCards from "../Cards/RocketCards.js";
import SearchForm from "../searchForm/SearchForm.js";
import SortForm from "../sortForm/SortForm.js";

const CatalogPage = ({ rockets, onSort }) => {
  const [filteredRockets, setFilteredRockets] = useState(rockets); // Стан для зберігання результатів пошуку
  const [isSortFormVisible, setSortFormVisible] = useState(false);

  // Оновлення стану filteredRockets при зміні rockets
  useEffect(() => {
    setFilteredRockets(rockets);
  }, [rockets]);

  const toggleSortForm = () => {
    setSortFormVisible((prev) => !prev);
  };

  // Функція для пошуку за ім'ям
  const handleSearch = (searchParams) => {
    const { name } = searchParams;
    const filtered = rockets.filter((rocket) =>
      name ? rocket.name.toLowerCase().includes(name.toLowerCase()) : true
    );
    setFilteredRockets(filtered); // Оновлюємо стан фільтрованих даних
  };

  return (
    <div className="catalog-page">
      <div className="forms-container">
        <SearchForm onSearch={handleSearch} />{" "}
        {/* Передаємо функцію handleSearch у SearchForm */}
        <button onClick={toggleSortForm} className="btn btn-primary mb-3">
          {isSortFormVisible ? "Hide Sort Options" : "Show Sort Options"}
        </button>
        {isSortFormVisible && <SortForm onSort={onSort} />}
      </div>
      <RocketCards rockets={filteredRockets} />{" "}
      {/* Відображаємо фільтровані дані */}
    </div>
  );
};

export default CatalogPage;
