import React, { useState } from "react";

const AddRocketForm = () => {
  // Створюємо стан для кожного поля форми
  const [name, setName] = useState("");
  const [manufacturer, setManufacturer] = useState("");
  const [country, setCountry] = useState("");
  const [countMission, setCountMission] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [aboutRocket, setAboutRocket] = useState("");

  // Обробка надсилання форми
  const handleSubmit = async (e) => {
    e.preventDefault(); // Запобігаємо перезавантаженню сторінки

    const newRocket = {
      name,
      manufacturer,
      country,
      count_mission: countMission, // Назва поля в базі даних
      image: imageUrl,
      video_url: videoUrl,
      about_rocket: aboutRocket,
    };

    try {
      const response = await fetch("http://localhost:8080/rockets", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newRocket), // Відправляємо нову ракету як JSON
      });

      if (response.ok) {
        alert("Rocket added successfully!");
        // Скидаємо поля після успішного додавання
        setName("");
        setManufacturer("");
        setCountry("");
        setCountMission("");
        setImageUrl("");
        setVideoUrl("");
        setAboutRocket("");
      } else {
        alert("Failed to add rocket.");
      }
    } catch (error) {
      console.error("Error adding rocket:", error);
    }
  };

  return (
    <div className="col-md-4">
      <div className="form">
        <form
          id="addRocketForm"
          className="rocket__form mb-4"
          onSubmit={handleSubmit}
        >
          <h2 className="mb-3 text-light">Add New Rocket</h2>

          {/* Поле для імені */}
          <div className="mb-2">
            <label htmlFor="rocket-name" className="form-label text-light">
              Name
            </label>
            <input
              type="text"
              className="form-control form-control-sm"
              id="rocket-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          {/* Поле для виробника */}
          <div className="mb-2">
            <label
              htmlFor="rocket-manufacturer"
              className="form-label text-light"
            >
              Manufacturer
            </label>
            <input
              type="text"
              className="form-control form-control-sm"
              id="rocket-manufacturer"
              value={manufacturer}
              onChange={(e) => setManufacturer(e.target.value)}
              required
            />
          </div>

          {/* Поле для країни */}
          <div className="mb-2">
            <label htmlFor="rocket-country" className="form-label text-light">
              Country
            </label>
            <input
              type="text"
              className="form-control form-control-sm"
              id="rocket-country"
              value={country}
              onChange={(e) => setCountry(e.target.value)}
              required
            />
          </div>

          {/* Поле для кількості місій */}
          <div className="mb-2">
            <label
              htmlFor="rocket-count-mission"
              className="form-label text-light"
            >
              Count Mission
            </label>
            <input
              type="number"
              min="0"
              className="form-control form-control-sm"
              id="rocket-count-mission"
              value={countMission}
              onChange={(e) => setCountMission(e.target.value)}
              required
            />
          </div>

          {/* Поле для зображення */}
          <div className="mb-2">
            <label htmlFor="rocket-image" className="form-label text-light">
              Image URL
            </label>
            <input
              type="text"
              className="form-control form-control-sm"
              id="rocket-image"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              required
            />
          </div>

          {/* Поле для відео */}
          <div className="mb-2">
            <label htmlFor="rocket-video-url" className="form-label text-light">
              Video URL
            </label>
            <input
              type="text"
              className="form-control form-control-sm"
              id="rocket-video-url"
              value={videoUrl}
              onChange={(e) => setVideoUrl(e.target.value)}
              required
            />
          </div>

          {/* Поле для опису */}
          <div className="mb-2">
            <label htmlFor="rocket-about" className="form-label text-light">
              About Rocket
            </label>
            <textarea
              className="form-control form-control-sm"
              id="rocket-about"
              value={aboutRocket}
              onChange={(e) => setAboutRocket(e.target.value)}
              required
            ></textarea>
          </div>

          <button type="submit" className="btn btn-success btn-sm">
            Add Rocket
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddRocketForm;
