import React, { useState, useEffect } from "react";
import "./Card.css";
import leftButton from "./images/left_button.svg";
import rightButton from "./images/right_button.svg";

const RocketCard = ({ rocketId, onPrevious, onNext }) => {
  const [rocket, setRocket] = useState(null);

  useEffect(() => {
    // Запит до сервера для отримання інформації про ракету
    const fetchRocketData = async () => {
      try {
        const response = await fetch(`/api/rockets/${rocketId}`); // API для витягнення ракети по ID
        const data = await response.json();
        setRocket(data);
      } catch (error) {
        console.error("Error fetching rocket data:", error);
      }
    };

    fetchRocketData();
  }, [rocketId]);

  if (!rocket) {
    return <p>Loading...</p>;
  }

  return (
    <div className="rocket-card position-relative">
      <div className="rocket-card__image">
        <img src={rocket.image} alt={rocket.name} />
      </div>
      <div className="rocket-card__info">
        <h2>{rocket.name}</h2>
        <p>
          <strong>Manufacturer:</strong> {rocket.manufacturer}
        </p>
        <p>
          <strong>Country:</strong> {rocket.country}
        </p>
        <p>
          <strong>Mission Count:</strong> {rocket.missionCount}
        </p>
        <p>{rocket.description}</p>

        <div className="rocket-card__about">
          <h3>About Rocket</h3>
          <p>{rocket.about}</p>
        </div>
      </div>
      <button
        id="left_button"
        className="btn position-absolute"
        style={{ left: "10px", top: "50%", transform: "translateY(-50%)" }}
        onClick={onPrevious}
      >
        <img src={leftButton} alt="Previous" />
      </button>

      <button
        id="right_button"
        className="btn position-absolute"
        style={{ right: "10px", top: "50%", transform: "translateY(-50%)" }}
        onClick={onNext}
      >
        <img src={rightButton} alt="Next" />
      </button>
    </div>
  );
};

export default RocketCard;
