import React, { useState, useEffect } from "react";
import RocketCards from "../Cards/RocketCards.js";
import SearchForm from "../searchForm/SearchForm.js";
import SortForm from "../sortForm/SortForm.js";

const CatalogPage = ({ rockets }) => {
  const [filteredRockets, setFilteredRockets] = useState(rockets); // Стан для зберігання результатів пошуку
  const [isSortFormVisible, setSortFormVisible] = useState(false);

  useEffect(() => {
    setFilteredRockets(rockets);
  }, [rockets]);

  const toggleSortForm = () => {
    setSortFormVisible((prev) => !prev);
  };
  const handleSort = (sortParams) => {
    let sortedRockets = [...filteredRockets];

    if (sortParams.order) {
      sortedRockets.sort((a, b) => {
        const aLaunches = a.launchCount || 0;
        const bLaunches = b.launchCount || 0;
        return sortParams.order === "asc"
          ? aLaunches - bLaunches
          : bLaunches - aLaunches;
      });
    }

    if (sortParams.launchMin) {
      const minLaunches = parseInt(sortParams.launchMin, 10);
      sortedRockets = sortedRockets.filter(
        (rocket) => (rocket.launchCount || 0) >= minLaunches
      );
    }

    if (sortParams.launchMax) {
      const maxLaunches = parseInt(sortParams.launchMax, 10);
      sortedRockets = sortedRockets.filter(
        (rocket) => (rocket.launchCount || 0) <= maxLaunches
      );
    }

    if (sortParams.country) {
      sortedRockets = sortedRockets.filter(
        (rocket) => rocket.country === sortParams.country
      );
    }

    if (sortParams.manufacturer) {
      sortedRockets = sortedRockets.filter(
        (rocket) => rocket.manufacturer === sortParams.manufacturer
      );
    }

    setFilteredRockets(sortedRockets);
  };

  return (
    <div className="catalog-page">
      <div className="forms-container">
        <SearchForm onSearch={handleSearch} />
        <button onClick={toggleSortForm} className="btn btn-primary mb-3">
          {isSortFormVisible ? "Hide Sort Options" : "Show Sort Options"}
        </button>
        {isSortFormVisible && <SortForm onSort={handleSort} />}
      </div>
      <RocketCards rockets={filteredRockets} />
    </div>
  );
};

export default CatalogPage;
