import React, { useEffect, useState } from "react";

function RocketCards() {
  const [rockets, setRockets] = useState([]); // Додаємо стан для збереження даних про ракети

  // Функція для отримання даних про ракети з серверу
  async function fetchRockets() {
    try {
      const response = await fetch("http://localhost:8080/rockets"); // Запит до серверу
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setRockets(data); // Оновлюємо стан отриманими даними
    } catch (error) {
      console.error("Error fetching rockets:", error);
    }
  }

  // Виклик fetchRockets при завантаженні компонента
  useEffect(() => {
    fetchRockets();
  }, []); // [] означає, що ефект буде виконаний тільки після першого рендеру

  return (
    <div className="rocket__cards">
      {rockets.length === 0 ? (
        <p>No rockets found</p>
      ) : (
        rockets.map((rocket) => (
          <div key={rocket.id} className="col-md-3 mb-4">
            <div className="card__rocket text-center p-3 bg-dark text-light">
              <h2 className="rocket__name">{rocket.name}</h2>
              <img
                src={rocket.image}
                alt={rocket.name}
                className="img-fluid mb-3"
              />
              <p>
                <strong>Manufacturer:</strong> {rocket.manufacturer}
              </p>
              <p>
                <strong>Country:</strong> {rocket.country}
              </p>
              <p>
                <strong>Count mission:</strong> {rocket.count_mission}
              </p>
              <p className="rocket__description">{rocket.about_rocket}</p>
              <button className="btn btn-primary mt-2">Watch Launch</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default RocketCards;
