import React, { useState, useEffect } from "react"; // Імпортуємо useState і useEffect
import Header from "./components/Header.js"; // Імпорт компонента Header
import RocketCards from "./components/RocketCards.js"; // Імпорт компонента RocketCards
import Modal from "./components/Modal.js"; // Імпорт компонента Modal
import RocketCard from "./components/RocketCard.js"; // Імпорт основної картки
import "./App.css"; // Підключення стилів

function App() {
  const [rockets, setRockets] = useState([]);

  // Імітуємо запит для отримання ракет
  useEffect(() => {
    const fetchRockets = async () => {
      const response = await fetch("http://localhost:8080/rockets"); // Додай свій реальний ендпойнт
      const data = await response.json();
      setRockets(data);
    };

    fetchRockets();
  }, []);

  // Дані для головної картки
  const mainRocket = {
    imageUrl: "https://example.com/rocket.jpg", // URL зображення головної картки
    name: "Falcon 9", // Назва
    country: "USA", // Країна
    manufacturer: "SpaceX", // Виробник
    launchCount: 5, // Кількість запусків
  };

  return (
    <div className="App">
      <Header />

      {/* Основна картка на всю ширину */}
      <div className="main-rocket-card">
        <RocketCard rocket={mainRocket} />
      </div>

      {/* Всі інші картки */}
      <RocketCards rockets={rockets} />

      <Modal />
    </div>
  );
}
useEffect(() => {
  const fetchMainRocket = async () => {
    try {
      const response = await fetch("http://localhost:8080/main-rocket"); // Заміни на реальний ендпойнт
      const data = await response.json();
      console.log(data); // Додай лог для перевірки отриманих даних
      setMainRocket(data); // Оновлюємо стан
    } catch (error) {
      console.error("Error fetching main rocket:", error); // Лог помилки
    }
  };

  fetchMainRocket();
}, []);

export default App;
