import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom"; // Додай маршрутизацію
import Header from "./components/Header/Header.js";
import RocketCards from "./components/Cards/RocketCards.js";
import Modal from "./components/Modal/Modal.js";
import RocketCard from "./components/Card/RocketCard.js";
import CatalogPage from "./pages/CatalogPage"; // Додай сторінку каталогу
import Footer from "./components/Footor/Footor.js";
import "./App.css";

function App() {
  const [rockets, setRockets] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchRockets = async () => {
      const response = await fetch("http://localhost:8080/rockets");
      const data = await response.json();
      setRockets(data);
    };

    fetchRockets();
  }, []);

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? rockets.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === rockets.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <Switch>
          <Route exact path="/">
            {/* Основна домашня сторінка */}
            {rockets.length > 0 && (
              <div className="main-rocket-card">
                <RocketCard
                  rocket={rockets[currentIndex]}
                  onPrevious={handlePrevious}
                  onNext={handleNext}
                />
              </div>
            )}
            <RocketCards rockets={rockets} />
          </Route>
          <Route path="/catalog">
            {/* Сторінка каталогу */}
            <CatalogPage rockets={rockets} />
          </Route>
          {/* Додай інші маршрути за потреби */}
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
