import React, { useState, useEffect } from "react";
import Header from "./components/Header.js";
import RocketCards from "./components/RocketCards.js";
import Modal from "./components/Modal.js";
import RocketCard from "./components/RocketCard.js";
import "./App.css";

function App() {
  const [rockets, setRockets] = useState([]);
  const [mainRocket, setMainRocket] = useState(null); // Стан для головної картки

  // Імітуємо запит для отримання ракет
  useEffect(() => {
    const fetchRockets = async () => {
      const response = await fetch("http://localhost:8080/rockets"); // Заміни на свій реальний ендпойнт
      const data = await response.json();
      setRockets(data);

      // Вибираємо першу ракету як основну (головну)
      if (data.length > 0) {
        setMainRocket(data[0]);
      }
    };

    fetchRockets();
  }, []);

  return (
    <div className="App">
      <Header />

      {/* Основна картка на 90% ширини */}
      {mainRocket && (
        <div className="main-rocket-card">
          <RocketCard rocket={mainRocket} />
        </div>
      )}

      {/* Всі інші картки */}
      <RocketCards rockets={rockets} />

      <Modal />
    </div>
  );
}

export default App;
