import React, { useState, useEffect } from "react"; // Імпортуємо useState і useEffect
import Header from "./components/Header.js"; // Імпорт компонента Header
import RocketCards from "./components/RocketCards.js"; // Імпорт компонента RocketCards
import Modal from "./components/Modal.js"; // Імпорт компонента Modal
import "./App.css"; // Підключення стилів

function App() {
  const [rockets, setRockets] = useState([]);

  // Функція для отримання всіх ракет або виконання пошуку і сортування
  const fetchRockets = async (params = {}) => {
    let query = "";

    // Додаємо параметри до запиту, якщо є
    const queryParams = new URLSearchParams();
    if (params.name) queryParams.append("name", params.name);
    if (params.country) queryParams.append("country", params.country);
    if (params.manufacturer)
      queryParams.append("manufacturer", params.manufacturer);
    if (params.launchMax) queryParams.append("launchMax", params.launchMax);
    if (params.launchMin) queryParams.append("launchMin", params.launchMin);
    if (params.order) queryParams.append("order", params.order);

    if (queryParams.toString()) {
      query = `?${queryParams.toString()}`;
    }

    try {
      const response = await fetch(`http://localhost:8080/rockets${query}`);
      const data = await response.json();
      setRockets(data);
    } catch (error) {
      console.error("Error fetching rockets:", error);
    }
  };

  // Викликаємо функцію для завантаження всіх ракет при першому завантаженні сторінки
  useEffect(() => {
    fetchRockets();
  }, []);

  return (
    <div className="App">
      <Header />
      <div className="forms-container">
        <SearchForm onSearch={fetchRockets} />
        <AddRocketForm />
        <SortForm onSort={fetchRockets} />
      </div>
      <RocketCards rockets={rockets} />
      <Modal />
    </div>
  );
}

export default App;
