import React, { useState, useEffect } from "react";
import Header from "./components/Header.js";
import RocketCards from "./components/RocketCards.js";
import Modal from "./components/Modal.js";
import RocketCard from "./components/RocketCard.js";
import "./App.css";
import Footer from "./components/Footer.js";

function App() {
  const [rockets, setRockets] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchRockets = async () => {
      try {
        const response = await fetch("http://localhost:8080/rockets");
        const data = await response.json();
        setRockets(data);
      } catch (error) {
        console.error("Error fetching rocket data:", error);
      }
    };

    fetchRockets();
  }, []);

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? rockets.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === rockets.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="App">
      <Header />

      {/* Основна картка на 90% ширини */}
      {rockets.length > 0 && (
        <div className="main-rocket-card">
          <RocketCard
            rocket={rockets[currentIndex]}
            onPrevious={handlePrevious}
            onNext={handleNext}
          />
        </div>
      )}

      {/* Всі інші картки */}
      <RocketCards rockets={rockets} />

      <Footer />
    </div>
  );
}

export default App;
