import React from "react";

const MaxLaunchInput = ({ value, onChange }) => {
  return (
    <div>
      <label htmlFor="launchMax">Max Launches</label>
      <input
        type="number"
        id="launchMax"
        value={value}
        onChange={(e) => onChange(e.target.value)} // Передаємо e.target.value
      />
    </div>
  );
};

export default MaxLaunchInput;
