// src/components/Home/Home.js
import React, { useState, useEffect } from "react";
import RocketCards from "../Cards/RocketCards.js";
import SearchForm from "../searchForm/SearchForm.js";
import SortForm from "../sortForm/SortForm.js";
import "./Home.css";

const Home = () => {
  const [rockets, setRockets] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  const fetchRockets = async (params = {}) => {
    let query = "";

    const queryParams = new URLSearchParams();
    if (params.name) queryParams.append("name", params.name);
    if (params.country) queryParams.append("country", params.country);
    if (params.manufacturer)
      queryParams.append("manufacturer", params.manufacturer);
    if (params.launchMax) queryParams.append("launchMax", params.launchMax);
    if (params.launchMin) queryParams.append("launchMin", params.launchMin);
    if (params.order) queryParams.append("order", params.order);

    if (queryParams.toString()) {
      query = `?${queryParams.toString()}`;
    }

    const response = await fetch(`/api/rockets${query}`);
    const data = await response.json();
    setRockets(data);
  };

  useEffect(() => {
    fetchRockets();
  }, []);

  return (
    <div className="home-page">
      <h1>Welcome to Rocket Catalog</h1>
      <div className="forms-container">
        <SearchForm onSearch={fetchRockets} />
        <SortForm onSort={fetchRockets} />
      </div>
      <RocketCards rockets={rockets} />
    </div>
  );
};

export default Home;
