// App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Header from "./components/Header/Header.js";
import RocketCard from "./components/Card/RocketCard.js";
import CatalogPage from "./components/Catalog/CatalogPage.js";
import Footer from "./components/Footor/Footor.js";
import "./App.css";

function App() {
  const [rockets, setRockets] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  const fetchRockets = async () => {
    const response = await fetch("/api/rockets");
    const data = await response.json();
    setRockets(data);
  };

  useEffect(() => {
    fetchRockets();
  }, []);

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex > 0 ? prevIndex - 1 : rockets.length - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex < rockets.length - 1 ? prevIndex + 1 : 0
    );
  };

  return (
    <Router>
      <Header />
      <Routes>
        <Route
          path="/"
          element={
            rockets.length > 0 && (
              <RocketCard
                rocket={rockets[currentIndex]}
                onPrevious={handlePrevious}
                onNext={handleNext}
              />
            )
          }
        />
        <Route path="/catalog" element={<CatalogPage rockets={rockets} />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
