import React, { useState, useEffect } from "react";
import Header from "./components/Header.js";
import SearchForm from "./components/SearchForm.js";
import AddRocketForm from "./components/AddRocketForm.js";
import RocketCards from "./components/RocketCards.js";
import Modal from "./components/Modal.js";
import SortForm from "./components/SortForm.js"; // Підключаємо SortForm
import "./App.css"; // Стилі

function App() {
  const [rockets, setRockets] = useState([]);

  // Функція для отримання всіх ракет або виконання пошуку/сортування
  const fetchRockets = async (searchParams = {}) => {
    let query = "";

    // Додаємо параметри до запиту, якщо є
    if (
      searchParams.name ||
      searchParams.country ||
      searchParams.manufacturer ||
      searchParams.launchMax ||
      searchParams.launchMin ||
      searchParams.order
    ) {
      query = `?name=${searchParams.name || ""}&country=${
        searchParams.country || ""
      }&manufacturer=${searchParams.manufacturer || ""}&launchMax=${
        searchParams.launchMax || ""
      }&launchMin=${searchParams.launchMin || ""}&order=${
        searchParams.order || ""
      }`;
    }

    try {
      const response = await fetch(`http://localhost:8080/rockets${query}`);
      const data = await response.json();
      setRockets(data);
    } catch (error) {
      console.error("Error fetching rockets:", error);
    }
  };

  // Викликаємо функцію для завантаження всіх ракет при першому завантаженні сторінки
  useEffect(() => {
    fetchRockets();
  }, []);

  return (
    <div className="App">
      <Header />
      <SearchForm onSearch={fetchRockets} /> {/* Пошук */}
      <SortForm onSort={fetchRockets} /> {/* Сортування */}
      <AddRocketForm /> {/* Додавання нової ракети */}
      <RocketCards rockets={rockets} /> {/* Відображення ракет */}
      <Modal />
    </div>
  );
}

export default App;
