import React, { useState, useEffect } from "react";
import RocketCards from "../Cards/RocketCards";
import SearchForm from "../searchForm/SearchForm";
import SortForm from "../sortForm/SortForm";

const CatalogPage = ({ rockets }) => {
  const [filteredRockets, setFilteredRockets] = useState(rockets);
  const [isSortFormVisible, setSortFormVisible] = useState(false);

  useEffect(() => {
    setFilteredRockets(rockets);
  }, [rockets]);

  const toggleSortForm = () => {
    setSortFormVisible((prev) => !prev);
  };

  const handleSearch = (searchParams) => {
    const { name } = searchParams;
    const filtered = rockets.filter((rocket) =>
      name ? rocket.name.toLowerCase().includes(name.toLowerCase()) : true
    );
    setFilteredRockets(filtered);
  };

  const handleSort = (sortParams) => {
    let sortedData = [...rockets];

    // Фільтрація за мінімальною та максимальною кількістю запусків
    if (sortParams.launchMin) {
      sortedData = sortedData.filter(
        (rocket) => rocket.count_mission >= Number(sortParams.launchMin)
      );
    }
    if (sortParams.launchMax) {
      sortedData = sortedData.filter(
        (rocket) => rocket.count_mission <= Number(sortParams.launchMax)
      );
    }

    // Фільтрація за країною
    if (sortParams.country) {
      sortedData = sortedData.filter(
        (rocket) => rocket.country === sortParams.country
      );
    }

    // Фільтрація за виробником
    if (sortParams.manufacturer) {
      sortedData = sortedData.filter(
        (rocket) => rocket.manufacturer === sortParams.manufacturer
      );
    }

    // Сортування за кількістю запусків
    if (sortParams.order) {
      sortedData.sort((a, b) =>
        sortParams.order === "asc"
          ? a.count_mission - b.count_mission
          : b.count_mission - a.count_mission
      );
    }

    setFilteredRockets(sortedData);
  };

  return (
    <div className="catalog-page">
      <div className="forms-container">
        <SearchForm onSearch={handleSearch} />
        <button onClick={toggleSortForm} className="btn btn-primary mb-3">
          {isSortFormVisible ? "Hide Sort Options" : "Show Sort Options"}
        </button>
        {isSortFormVisible && <SortForm onSort={handleSort} />}
      </div>
      <RocketCards rockets={filteredRockets} />
    </div>
  );
};

export default CatalogPage;
